import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const wholeData = require("./Rule.json");
  const [data, setData] = useState([...wholeData]);
  const [current, setCurrent] = useState(40);

  useEffect(() => {
    setData(wholeData);
  }, []);

  function deleteRow(i) {
    delete data[i];
    setData(data.filter((item, index) => index !== i));
  }

  function cloneRow(i) {
    data.push(data[i]);
    setData([...data]);
  }

  function moveUp(i) {
    const object = data[i - 1];
    data[i - 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  function moveDown(i) {
    const object = data[i + 1];
    data[i + 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  window.addEventListener("scroll", (e) => {
    setCurrent(current + 20);
  });

  return (
    <table className="content">
      <tr className="row">
        <th>Rule Id</th>
        <th>Rule Name</th>
        <th>Delete</th>
        <th>Clone</th>
        <th>Up</th>
        <th>Down</th>
      </tr>
      {data.map((entry, index) => {
        return (
          <tr key={index} className="row">
            <td>{entry.id}</td>
            <td>{entry.ruleName}</td>
            <td>
              <img
                src="./delete.png"
                alt="delete"
                onClick={() => deleteRow(index)}
              />
            </td>
            <td>
              <img
                src="./clone.png"
                alt="clone"
                onClick={() => cloneRow(index)}
              />
            </td>
            <td>
              <img
                src="./moveup.png"
                alt="moveup"
                onClick={() => moveUp(index)}
              />
            </td>
            <td>
              <img
                src="./movedown.png"
                alt="movedown"
                onClick={() => moveDown(index)}
              />
            </td>
          </tr>
        );
      })}
    </table>
  );
}

export default App;
