import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("./Rule JSON.json")
      .then((response) => response.json())
      .then((item) => setData(item.data));
  }, []);

  function deleteRow(i) {
    delete data[i];
    console.log(data);
  }

  function cloneRow(i) {
    data.push(data[i]);
    console.log(data);
  }

  function moveUp(i) {
    const object = data[i];
    console.log(object);
    console.log(data);
  }

  function moveDown(i) {
    delete data[i];
    console.log(data);
  }

  return (
    <>
      {data.map((entry, index) => {
        return (
          <tr key={entry.id} className="row">
            <td>{entry.id}</td>
            <td>{entry.ruleName}</td>
            <td>
              <button onClick={() => deleteRow(index)}>Delete</button>
            </td>
            <td>
              <button onClick={() => cloneRow(index)}>Clone</button>
            </td>
            <td>
              <button onClick={() => moveUp(index)}>Move Up</button>
            </td>
            <td>
              <button onClick={() => moveDown(index)}>Move Down</button>
            </td>
          </tr>
        );
      })}
    </>
  );
}

export default App;
