import { useEffect, useState } from "react";
import "./App.css";
import InfiniteScroll from "react-infinite-scroll-component";

function App() {
  const wholeData = require("./Rule.json");
  const [data] = useState(wholeData.data);
  const [count, setCount] = useState({
    prev: 0,
    next: 20,
  });
  const [currentData, setCurrentData] = useState(
    data.splice(count.prev, count.next)
  );
  const [hasMoreData, setHasMoreData] = useState(true);

  function deleteRow(i) {
    delete data[i];
    setCurrentData(data.splice(count.prev, count.next));
  }

  function cloneRow(i) {
    currentData.push(data[i]);
    setCurrentData([...currentData]);
  }

  function moveUp(i) {
    const object = currentData[i - 1];
    currentData[i - 1] = currentData[i];
    currentData[i] = object;
    setCurrentData([...currentData]);
  }

  function moveDown(i) {
    const object = currentData[i + 1];
    currentData[i + 1] = currentData[i];
    currentData[i] = object;
    setCurrentData([...currentData]);
  }

  function getMoreData() {
    if (currentData.length === data.length) {
      setHasMoreData(false);
    }
    setTimeout(() => {
      setCurrentData(
        currentData.concat(data.splice(count.prev + 10, count.next + 10))
      );
    }, 2000);
    setCount((prevState) => ({
      prev: prevState.prev + 10,
      next: prevState.next + 10,
    }));
  }

  return (
    <InfiniteScroll
      dataLength={currentData.length}
      next={getMoreData}
      hasMore={hasMoreData}
      loader={<h4>Loading...</h4>}
    >
      <table className="content">
        <tr className="row">
          <th>Rule Id</th>
          <th>Rule Name</th>
          <th>Delete</th>
          <th>Clone</th>
          <th>Up</th>
          <th>Down</th>
        </tr>
        {currentData &&
          currentData.map((entry, index) => {
            return (
              <tr key={index} className="row">
                <td>{entry.id}</td>
                <td>{entry.ruleName}</td>
                <td>
                  <img
                    src="./delete.png"
                    alt="delete"
                    onClick={() => deleteRow(index)}
                  />
                </td>
                <td>
                  <img
                    src="./clone.png"
                    alt="clone"
                    onClick={() => cloneRow(index)}
                  />
                </td>
                <td>
                  <img
                    src="./moveup.png"
                    alt="moveup"
                    onClick={() => moveUp(index)}
                  />
                </td>
                <td>
                  <img
                    src="./movedown.png"
                    alt="movedown"
                    onClick={() => moveDown(index)}
                  />
                </td>
              </tr>
            );
          })}
      </table>
    </InfiniteScroll>
  );
}

export default App;
