import { useEffect, useState } from "react";
import "./App.css";

function App1() {
  const [data, setData] = useState([]);
  const [prevCount, setPrevCount] = useState(0);
  const [current, setCurrent] = useState(40);

  const handleScroll = () => {
    let lastTr = document.querySelector(".content > td:last-child");
    let lastTrOffset = lastTr.offsetTop + lastTr.clientHeight;
    let pageOffset = window.pageYOffset + window.innerHeight;
    if (pageOffset > lastTrOffset) {
      loadMore();
    }
  };

  const loadMore = () => {
    setPrevCount(prevCount + 40);
    setCurrent(current + 40);
    loadUser();
  };

  const loadUser = () => {
    fetch("./Rule.json")
      .then((response) => response.json())
      .then((json) => json.data)
      .then((item) =>
        setData(item.filter((i, index) => prevCount >= 0 && index < current))
      );
  };

  useEffect(() => {
    loadUser();
    scrollListener = window.addEventListener("scroll", (e) => {
      handleScroll();
    });
  }, []);

  return (
    <table className="content">
      <tr className="row">
        <th>Rule Id</th>
        <th>Rule Name</th>
      </tr>
      {data.map((entry, index) => {
        return (
          <tr key={entry.id} className="row">
            <td>{entry.id}</td>
            <td>{entry.ruleName}</td>
          </tr>
        );
      })}
    </table>
  );
}

export default App1;
