import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("./Rule JSON.json")
      .then((response) => response.json())
      .then((item) => setData(item.data));
  }, []);

  function deleteRow(i) {
    delete data[i];
    console.log(data);
  }

  function cloneRow(i) {
    data.push(data[i]);
    console.log(data);
  }

  function moveUp(i) {
    const object = data[i - 1];
    data[i - 1] = data[i];
    data[i] = object;
    console.log(data);
  }

  function moveDown(i) {
    const object = data[i + 1];
    data[i + 1] = data[i];
    data[i] = object;
    console.log(data);
  }

  return (
    <div>
      {data.map((entry, index) => {
        return (
          <table className="content row">
            <tr key={index}>
              <td>{entry.id}</td>
              <td>{entry.ruleName}</td>
              <td>
                <img
                  src="./delete.png"
                  alt="delete"
                  onClick={() => deleteRow(index)}
                />
              </td>
              <td>
                <img
                  src="./clone.png"
                  alt="clone"
                  onClick={() => cloneRow(index)}
                />
              </td>
              <td>
                <img
                  src="./moveup.png"
                  alt="moveup"
                  onClick={() => moveUp(index)}
                />
              </td>
              <td>
                <img
                  src="./movedown.png"
                  alt="movedown"
                  onClick={() => moveDown(index)}
                />
              </td>
            </tr>
          </table>
        );
      })}
    </div>
  );
}

export default App;
