import { useEffect, useState } from "react";
import "./App.css";

function App() {
  let boxElement;
  const [data, setData] = useState([]);
  const [current, setCurrent] = useState(40);

  useEffect(() => {
    fetch("./Rule.json/_limit=10")
      .then((response) => response.json())
      .then((json) => json.data)
      .then((item) =>
        setData(item.filter((i, index) => index < current && i.ruleName))
      );
  }, [current]);

  function deleteRow(id) {
    setData(data.filter((item) => item.id !== id));
  }

  function cloneRow(id) {
    data.push(data.filter((item) => item.id === id));
    setData([...data]);
  }

  function moveUp(i) {
    const object = data[i - 1];
    data[i - 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  function moveDown(i) {
    const object = data[i + 1];
    data[i + 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  function createObserver() {
    let observer;

    let options = {
      root: null,
      rootMargin: "0px",
      threshold: 1,
    };

    observer = new IntersectionObserver(handleIntersect, options);
    observer.observe(boxElement);
  }

  function handleIntersect(entries, observer) {
    entries.forEach((entry) => {
      if (entry.intersectionRatio > prevRatio) {
        entry.target.style.backgroundColor = increasingColor.replace(
          "ratio",
          entry.intersectionRatio
        );
      } else {
        entry.target.style.backgroundColor = decreasingColor.replace(
          "ratio",
          entry.intersectionRatio
        );
      }

      prevRatio = entry.intersectionRatio;
    });
  }

  window.addEventListener("load", (e) => {
    boxElement = document.querySelector("content");
    createObserver();
  });

  return (
    <table className="content">
      <tr className="row">
        <th>Rule Id</th>
        <th>Rule Name</th>
        <th>Delete</th>
        <th>Clone</th>
        <th>Up</th>
        <th>Down</th>
      </tr>
      {data.map((entry, index) => {
        return (
          <tr key={entry.id} className="row">
            <td>{entry.id}</td>
            <td>{entry.ruleName}</td>
            <td>
              <img
                src="./delete.png"
                alt="delete"
                onClick={() => deleteRow(entry.id)}
              />
            </td>
            <td>
              <img
                src="./clone.png"
                alt="clone"
                onClick={() => cloneRow(entry.id)}
              />
            </td>
            <td>
              <img
                src="./moveup.png"
                alt="moveup"
                onClick={() => moveUp(index)}
              />
            </td>
            <td>
              <img
                src="./movedown.png"
                alt="movedown"
                onClick={() => moveDown(index)}
              />
            </td>
          </tr>
        );
      })}
    </table>
  );
}

export default App;
