import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [data, setData] = useState([]);
  const [prevCount, setPrevCount] = useState(0);
  const [current, setCurrent] = useState(40);

  useEffect(() => {
    fetch("./Rule JSON.json")
      .then((response) => response.json())
      .then((json) => json.data)
      .then((item) =>
        setData(
          item.filter((item, index) => index > prevCount && index < current)
        )
      );
  }, [current]);

  function deleteRow(i) {
    delete data[i];
    setData(data.filter((item, index) => index !== i));
  }

  function cloneRow(i) {
    data.push(data[i]);
    setData([...data]);
  }

  function moveUp(i) {
    const object = data[i - 1];
    data[i - 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  function moveDown(i) {
    const object = data[i + 1];
    data[i + 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  window.addEventListener("scroll", (e) => {
    let element = e.target;
    if (element.scrollHeight - element.scrollTop === element.clientHeight) {
      setPrevCount(prevCount + 20);
      setCurrent(current + 20);
    }
  });

  return (
    <table className="content">
      <tr className="row">
        <th>Rule Id</th>
        <th>Rule Name</th>
        <th>Delete</th>
        <th>Clone</th>
        <th>Up</th>
        <th>Down</th>
      </tr>
      {data.map((entry, index) => {
        return (
          <tr key={index} className="row">
            <td>{entry.id}</td>
            <td>{entry.ruleName}</td>
            <td>
              <img
                src="./delete.png"
                alt="delete"
                onClick={() => deleteRow(index)}
              />
            </td>
            <td>
              <img
                src="./clone.png"
                alt="clone"
                onClick={() => cloneRow(index)}
              />
            </td>
            <td>
              <img
                src="./moveup.png"
                alt="moveup"
                onClick={() => moveUp(index)}
              />
            </td>
            <td>
              <img
                src="./movedown.png"
                alt="movedown"
                onClick={() => moveDown(index)}
              />
            </td>
          </tr>
        );
      })}
    </table>
  );
}

export default App;
