import { useEffect, useState } from "react";
import "./App.css";

function App1() {
  const [data, setData] = useState([]);
  const [count, setCount] = useState({ prev: 0, current: 40 });

  const handleScroll = () => {
    let lastTr = document.querySelector(".content > tr:last-child");
    console.log(lastTr.offsetTop);
    console.log(lastTr.clientHeight);
    console.log(window.pageYOffset);
    console.log(window.innerHeight);
    let lastTrOffset = lastTr.offsetTop + lastTr.clientHeight;
    let pageOffset = window.pageYOffset + window.innerHeight;
    if (pageOffset > lastTrOffset) {
      console.log("coming here");
      loadMore();
    }
  };

  const loadMore = () => {
    setCount(
      { prev: count.prev + 40, current: count.current + 40 },
      loadUser()
    );
  };

  const loadUser = () => {
    console.log(current + "    " + prevCount);
    fetch("./Rule.json")
      .then((response) => response.json())
      .then((json) => json.data)
      .then((item) =>
        setData(
          item.filter(
            (i, index) => index >= count.prev && index < count.current
          )
        )
      );
  };

  useEffect(() => {
    loadUser();
    window.addEventListener("scroll", (e) => {
      handleScroll(e);
    });
  }, []);

  return (
    <table className="content">
      <tr className="row">
        <th>Rule Id</th>
        <th>Rule Name</th>
      </tr>
      {data.map((entry, index) => {
        return (
          <tr key={entry.id} className="row">
            <td>{entry.id}</td>
            <td>{entry.ruleName}</td>
          </tr>
        );
      })}
    </table>
  );
}

export default App1;
