import { useEffect, useState } from "react";
import "./App.css";
import InfiniteScroll from "react-infinite-scroll-component";

function App() {
  const [data, setData] = useState([]);
  const [count, setCount] = useState({
    prev: 0,
    next: 10,
  });
  const [currentData, setCurrentData] = useState(
    data.splice(count.prev, count.next)
  );
  const [hasMoreData, setHasMoreData] = useState(true);

  useEffect(() => {
    fetch("./Rule JSON.json")
      .then((response) => response.json())
      .then((json) => setData(json.data));
  }, []);

  function deleteRow(i) {
    delete data[i];
    setData(data.filter((item, index) => index !== i));
  }

  function cloneRow(i) {
    data.push(data[i]);
    setData([...data]);
  }

  function moveUp(i) {
    const object = data[i - 1];
    data[i - 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  function moveDown(i) {
    const object = data[i + 1];
    data[i + 1] = data[i];
    data[i] = object;
    setData([...data]);
  }

  function getMoreData() {
    if (currentData.length === data.length) {
      setHasMoreData(false);
    }
    setTimeout(() => {
      setCurrentData(
        currentData.concat(data.splice(count.prev + 10, count.next + 10))
      );
    }, 2000);
    setCount((prevState) => ({
      prev: prevState.prev + 10,
      next: prevState.next + 10,
    }));
  }

  return (
    <InfiniteScroll
      dataLength={currentData.length}
      next={getMoreData}
      hasMore={hasMoreData}
      loader={<h4>Loading...</h4>}
    >
      <table className="content">
        <tr className="row">
          <th>Rule Id</th>
          <th>Rule Name</th>
          <th>Delete</th>
          <th>Clone</th>
          <th>Up</th>
          <th>Down</th>
        </tr>
        {currentData.map((entry, index) => {
          return (
            <tr key={index} className="row">
              <td>{entry.id}</td>
              <td>{entry.ruleName}</td>
              <td>
                <img
                  src="./delete.png"
                  alt="delete"
                  onClick={() => deleteRow(index)}
                />
              </td>
              <td>
                <img
                  src="./clone.png"
                  alt="clone"
                  onClick={() => cloneRow(index)}
                />
              </td>
              <td>
                <img
                  src="./moveup.png"
                  alt="moveup"
                  onClick={() => moveUp(index)}
                />
              </td>
              <td>
                <img
                  src="./movedown.png"
                  alt="movedown"
                  onClick={() => moveDown(index)}
                />
              </td>
            </tr>
          );
        })}
      </table>
    </InfiniteScroll>
  );
}

export default App;
